mapper.select:
SELECT * FROM BUSINESSPLANA
WHERE ID=#{id}
ORDER BY WRITEDATE DESC

mapper.update:
UPDATE BUSINESSPLANA 

	BUSINESS_A =#{business_a},
	BUSINESS_B=#{business_b},
	BUSINESS_C=#{business_c},
	title=#{title}


WHERE SEQ=#{seq}

UPDATE BUSINESSPLANB

	prod_a =#{prod_a},
	prod_b=#{prod_b},
	prod_c=#{prod_c}

WHERE SEQ=#{seq}

UPDATE BUSINESSPLANC
market_a =#{market_a}, market_b=#{market_b}, market_c=#{market_c}, market_d=#{market_d},
WHERE SEQ=#{seq}

UPDATE BUSINESSPLAND

	selling_a =#{selling_a},
	selling_b=#{selling_b},
	selling_c=#{selling_c}

WHERE SEQ=#{seq}

UPDATE BUSINESSPLANA 
SET MENTORID=#{mentorId}
WHERE SEQ=#{seq}

mapper.select:
SELECT COUNT(*) 
FROM BUSINESSPLANA 
WHERE MENTORID=#{mentorId}

SELECT COUNT(*) 
FROM RESUME 
WHERE MENTORID=#{mentorId}

SELECT COLLECTION 
FROM BUSINESSPLANA 
WHERE SEQ=#{seq}
